mapper.select:
		select 
	    from sys_users a
	    where 1=1
    	and a.username like CONCAT('%',#{username},'%')
	    and a.nickname like CONCAT('%',#{nickname},'%') 
        and a.gender = #{gender}
        and DATE_FORMAT(a.create_time, '%Y-%m-%d')  >= #{beginCreateTime}
        and DATE_FORMAT(a.create_time, '%Y-%m-%d')  <= #{endCreateTime}
    	order by a.id asc , a.gender asc
